<template>
  <q-page padding>
      <q-page-sticky position="top-right">
      <q-select
        filter
        inset
        autofocus-filter
        filter-placeholder="search"
        placeholder="Input Script"
        float-label="Guide Script"
        v-model="script2"
        class="q-ma-sm col-md-3"
        :options="scriptsOutput"
      />
    </q-page-sticky>
<h4> {{getScriptObject(script1).label}} <q-spinner-comment color="dark" :size="30" v-show="loading"/> </h4>
      <div class="q-body-1" v-html="getDescription(getScriptObject(script1))">
      </div> <br/> <q-chip class="q-ma-xs" color="dark" v-for="tag in tags"
      :key="tag" small tag> {{tag}} </q-chip><br/> <br/>
<q-tabs color="tertiary" no-pane-border inverted position="top">
  <!-- Tabs - notice slot="title" -->
  <q-tab default slot="title" name="tab-1" icon="info" label="Overview" class="print-hide"/> <br/>
  <q-tab slot="title" name="tab-2" icon="keyboard" label="Syllabary" class="print-hide" v-if="!['IPA', 'RussianCyrillic'].includes(script1)"/>
  <q-tab slot="title" name="tab-3" icon="keyboard" label="Conjuncts" class="print-hide"  v-if="!['IPA', 'RussianCyrillic', 'Hiragana', 'Katakana', 'Hebrew'].includes(script1)"/>

  <q-tab-pane name="tab-1" keep-alive>
      <div style="text-align: right">
      <span class="text-red-2"> X</span> : Approximate equivalent <br/>
      <span class="text-blue-4"> Y</span> : Equivalent with diacritic <br/>
      </div>
      <h5> Vowels</h5>
      <list-char :chars="charList[0]" :script1="script1" :script2="script2" :chars1="charListC['script1'][0]"
        :chars2="charListC['script2'][0]" :charsIr="charListC['script1hk'][0]"> </list-char>
      <b> Others </b> <br/><br/>
      <list-char :chars="charList[1]" :script1="script1" :script2="script2" :chars1="charListC['script1'][1]"
        :chars2="charListC['script2'][1]" :charsIr="charListC['script1hk'][1]"> </list-char>
      <b> Combining Signs </b> <br/><br/>
      <list-char :chars="charList[2]" :script1="script1" :script2="script2" :chars1="charListC['script1'][2]"
        :chars2="charListC['script2'][2]" :charsIr="charListC['script1hk'][2]"> </list-char>
      <h5> Consonants </h5>
      <list-char :chars="chars" :script1="script1" :script2="script2" v-for="(chars, index) in charList[3]" :key="index"
      :chars1="charListC['script1'][3][index]" :chars2="charListC['script2'][3][index]" :charsIr="charListC['script1hk'][3][index]"> </list-char>
      <b> South-Indic Consonants </b> <br/><br/>
      <list-char :chars="charList[4]" :script1="script1" :script2="script2" :chars1="charListC['script1'][4]"
        :chars2="charListC['script2'][4]" :charsIr="charListC['script1hk'][4]"> </list-char>
      <b> North-Indic Consonants with Nukta </b> <br/><br/>
      <list-char :chars="charList[5]" :script1="script1" :script2="script2" :chars1="charListC['script1'][5]"
        :chars2="charListC['script2'][5]" :charsIr="charListC['script1hk'][5]"> </list-char>
      <b> Sinhala Pre-nasalized Consonants </b> <br/><br/>
      <list-char :chars="charList[6]" :script1="script1" :script2="script2" :chars1="charListC['script1'][6]"
        :chars2="charListC['script2'][6]" :charsIr="charListC['script1hk'][6]"> </list-char>
      <h5> CV Syllables with KA </h5>
      <list-char :chars="charList[10]" :script1="script1" :script2="script2" :chars1="charListC['script1'][10]"
        :chars2="charListC['script2'][10]" :charsIr="charListC['script1hk'][10]"> </list-char>
      <list-char :chars="charList[11]" :script1="script1" :script2="script2" :chars1="charListC['script1'][11]"
        :chars2="charListC['script2'][11]" :charsIr="charListC['script1hk'][11]"> </list-char>
      <list-char :chars="charList[12]" :script1="script1" :script2="script2" :chars1="charListC['script1'][12]"
        :chars2="charListC['script2'][12]" :charsIr="charListC['script1hk'][12]"> </list-char>
      <h5> Conjuncts with KA</h5>
      <list-char :chars="charList[9]" :script1="script1" :script2="script2" :chars1="charListC['script1'][9]"
        :chars2="charListC['script2'][9]" :charsIr="charListC['script1hk'][9]"> </list-char>
      <h5> Numerals </h5>
      <list-char :chars="charList[7]" :script1="script1" :script2="script2" :chars1="charListC['script1'][7]"
        :chars2="charListC['script2'][7]" :charsIr="charListC['script1hk'][7]"> </list-char>
      <h5> Others </h5>
      <list-char :chars="charList[8]" :script1="script1" :script2="script2" :chars1="charListC['script1'][8]"
        :chars2="charListC['script2'][8]" :charsIr="charListC['script1hk'][8]"> </list-char>

        <div v-show="typeof textNative[script1] !== 'undefined'">
        <h5> {{getScriptObject(script1).label}} Text </h5>
        <div v-html="textNative[script1]" :class="script1.toLowerCase()"></div><br/>
        <transliterate :text="textNative[script1]" :src="script1" :tgt="script2" :sourcePreserve="false" :postOptions="[]"
        :preOptions="script1 === 'Urdu' || script1 == 'Shahmukhi'? ['UrduShortNotShown'] : []">
        </transliterate>
      </div>
      <h5> Sanskrit Text </h5>
      <transliterate :text="quoteSa" src="HK" :tgt="script2" :sourcePreserve="true" :postOptions="[]">
      </transliterate> <br/>
      <transliterate :text="quoteSa" src="HK" :tgt="script1" :sourcePreserve="false" :postOptions="[]">
      </transliterate>
      <h5> Pali Text </h5>
      <transliterate :text="quotePa" src="HK" :tgt="script2" :sourcePreserve="true" :postOptions="[]">
      </transliterate> <br/>
      <transliterate :text="quotePa" src="HK" :tgt="script1" :sourcePreserve="false" :postOptions="[]">
      </transliterate>
      <div v-if="getScriptObject(script1).font.name !== ''">
      <h5> Font </h5>
      <div class="q-body-1">The font used is {{getScriptObject(script1).font.name}}, which can be downloaded from <a :href="getScriptObject(script1).font.url">here</a>.</div>
      </div>
      <h5> Notes </h5>
      <div class="q-body-1">For general character mapping notes, please refer to <router-link to="/script-matrix">Script Matrix </router-link>.</div><br/>
      <div class="q-body-1" v-html="notes[script1]"> </div>
      <h5 v-if="links[script1]"> Related Links </h5>
      <ol v-if="links[script1]" class="q-body-1">
      <li v-for="link in links[script1]" :key="link"> <a :href="link"> {{link}} </a> </li>
      </ol>
  </q-tab-pane>
  <q-tab-pane name="tab-2" keep-alive>
    <syllabary :script1="script1" :script2="script2"> </syllabary>
  </q-tab-pane>
  <q-tab-pane name="tab-3" keep-alive>
    <conjuncts :script1="script1" :script2="script2"> </conjuncts>
  </q-tab-pane>

</q-tabs>
  </q-page>
</template>

<style scoped>
h4 {
  margin-top: -20px;
}
</style>

